public class ControlStatement {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x=6;
		if(x!=5)
		{
			System.out.println("the value of x is not 5");
		}
		else
		{
			System.out.println("The value of x is 5");
		}

	}

}
